function FND(SearchWord){
var ResFound=false;
var SamePage=false;
var Result1="";
var Result2="";
var Result3="";
var NrRes=0;
Result1+="<HTML>\n";
Result1+="<HEAD>\n";
Result1+="<TITLE>Pretraga</TITLE>\n";
Result1+="<meta http-equiv='Content-Type' content='text/html; charset=windows-1252'>\n";
Result1+="<LINK REL='stylesheet' TYPE='text/css' HREF='Styles.css'>\n";
Result1+="</HEAD>\n";
Result1+="<BODY background='BackGround.gif'>\n";
if(SearchWord.length>=1){SearchWord=SearchWord.toLowerCase();
while(SearchWord.indexOf("<")>-1 || SearchWord.indexOf(">")>-1 || SearchWord.indexOf('"')>-1){SearchWord=SearchWord.replace("<","&lt;").replace(">","&gt;").replace('"',"&quot;");}
this.status="Pretraga je u toku, molimo sacekajte ...";BeginTime=new Date();
Result1+="<center><table cellpadding='10' class='t' width='95%'><tr><td>";
for(j=0;j<202;j++){k=Page[j].length-1;SamePage=false;LineNr=0;
for(i=0;i<k-2;i++){WordPos=Page[j][i].toLowerCase().indexOf(SearchWord);
if(WordPos>-1){
FoundWord=Page[j][i].substr(WordPos,SearchWord.length);
if(!SamePage){NrRes++;
Result3+="<P>";
Result3+=NrRes+". ";
Result3+="<a class='l' href='"+Page[j][k-2]+"'>"+Page[j][k-3].replace(FoundWord,FoundWord.bold())+"</a><BR>\n";
}SamePage=true;
if(i<k-3){LineNr++;if(LineNr>2){break;}
if(Page[j][i].length>350){Result3+="<LI>..."+Page[j][i].substr(WordPos-100,200+FoundWord.length).replace(FoundWord,FoundWord.bold())+"...\n";}
else{Result3+="<LI>"+Page[j][i].replace(FoundWord,FoundWord.bold())+"\n";}}
ResFound=true;}}}
if(!ResFound){
Result3+="<CENTER><U>vasa pretraga je uzvratila 0 rezultata!</U></CENTER>\n";}
Result3+="<P><CENTER>"
Result3+="<a class='h' href='#'>idi na vrh stranice </a>\n";
Result3+="</CENTER></td></tr></table></center></BODY></HTML>";
Result2="<center><B>"+NrRes+"</B> rezultat(a) pretrage";
Result2+="  <B>"+SearchWord+"</B>. Pretraga je trajala <B>";
Result2+=(Math.floor((new Date()-BeginTime)/10)/100)+"</B> sekundi.</center><P>";
this.status="Done";
this.document.open();
this.document.write(Result1+Result2+Result3);
this.document.close();}
else{this.status="Molimo unesite rijec za pretragu ! 1 ";}}
